import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Form,
  Input,
  InputNumber,
  Card,
  Button,
  Typography,
  Row,
  Col,
  Image,
  Select,
  Alert,
} from "antd";

const api = require("../../../utils/api/actors");
const { Text } = Typography;
const { TextArea } = Input;
const { Option } = Select;

const options = [
  { value: "Burns Bay Road" },
  { value: "Downing Street" },
  { value: "Wall Street" },
];
const ActorsAdd = () => {
  const navigate = useNavigate();

  const [previewer, setPreviewer] = useState("error");
  const [loading, setLoading] = useState(false);

  const handleChange = (event: any) => {
    setPreviewer(event.target.value);
  };

  const onSubmit = async (data: any) => {
    // setConfirmLoading(false);

    const payload = {
      firstName: data.firstName,
      lastName: data.lastName,
      imageUrl: data.imageUrl,
      gender: data.gender,
      age: data.age,
    };

    setLoading(true);
    let res = await api.addActors(payload);
    if (res) {
      return navigate("/manage/actors");
    }
    return setLoading(false);
  };
  

  return (
    <Card title="Add Actors" size="small" style={{ margin: "25px" }}>
      <Form
        style={{ margin: "25px" }}
        name="basic"
        labelCol={{ span: 6 }}
        wrapperCol={{ span: 12 }}
        initialValues={{ remember: true }}
        onFinish={onSubmit}
        autoComplete="off"
      >
        <Row>
          <Col span={14}>
            <Form.Item
              label="Image URL"
              name="imageUrl"
              rules={[
                {
                  required: true,
                  type: "url",
                  message: "Image URL is required!",
                },
              ]}
            >
              <Input onChange={handleChange} />
            </Form.Item>
            <Form.Item
              name="firstName"
              label="First Name"
              rules={[
                {
                  required: true,
                  message: "First name is required!",
                },
              ]}
            >
              <Input />
            </Form.Item>

            <Form.Item
              name="lastName"
              label="Last Name"
              rules={[
                {
                  required: true,
                  message: "Last name is required",
                },
              ]}
            >
              <Input />
            </Form.Item>
            <Form.Item
              name="gender"
              label="Gender"
              rules={[{ required: true, message: "Please select gender!" }]}
            >
              <Select placeholder="Select gender">
                <Option value="male">Male</Option>
                <Option value="female">Female</Option>
              </Select>
            </Form.Item>
            <Form.Item
              label="Age"
              name="age"
              rules={[
                {
                  required: true,
                  message: "Age is required!",
                },
              ]}
            >
              <InputNumber min={1} max={150} />
            </Form.Item>
            <Form.Item wrapperCol={{ offset: 6 }}>
              <Button type="primary" htmlType="submit">
                Add Actors
              </Button>
            </Form.Item>
          </Col>
          <Col span={1}>
            <Image
              style={{
                border: "3px solid #f5f5f5",
                backgroundRepeat: "no-repeat",
                backgroundSize: "370px 500px",
                backgroundImage: `url("data:image/png;base64,")`,
              }}
              width={470}
              height={600}
              src={previewer}
              fallback="data:image/png;base64,"
            />
          </Col>
        </Row>
      </Form>
    </Card>
  );
};
export default ActorsAdd;
